import React, {useState}  from 'react';
import './App.css'

const questoes=[
  {
    questionText: "Qual é o idioma de Angola ?",
    answerOptions:[
      {answerText: "Portugues", isCorrect:true},
      {answerText: "Frances", isCorrect:false},
      {answerText: "Ingles", isCorrect:false},
      {answerText: "Alemão", isCorrect:false},
    ],
  },
  {
    questionText: "Quem foi o primeiro presidente de Angola ?",
    answerOptions:[
      {answerText: "António Agostinho Neto", isCorrect:true},
      {answerText: "Jónas Savimbe", isCorrect:false},
      {answerText: "José Eduardo dos Santos", isCorrect:false},
      {answerText: "João Lourenço", isCorrect:false},
    ],
  },
  {
    questionText: "Qual empresa criou o Windows ?",
    answerOptions:[
      {answerText: "Microsoft", isCorrect:true},
      {answerText: "Amazom", isCorrect:false},
      {answerText: "Aple", isCorrect:false},
      {answerText: "Facebook", isCorrect:false},
    ],
  },
  {
    questionText: "Qual é a linguagem mais falada no mundo ?",
    answerOptions:[
      {answerText: "Portugues", isCorrect:true},
      {answerText: "Frances", isCorrect:false},
      {answerText: "Ingles", isCorrect:false},
      {answerText: "Alemão", isCorrect:false},
    ],
  },
]

function App() {
  const [showScore, setShowScore]= useState(false);
  const [score, setScore]= useState(0);
  const [currentQuestion, setCurrentQuestion]= useState(0);

  return (

      <div className='App-container'>
        {showScore ? (
          <div className='score-section'>
            Voce pontoou {score} de {questoes.length} perguntas
          </div>
        ):(
          <>
          <div className='group-questions'>
            <div>
               <span>Questão {currentQuestion+1}</span> /{questoes.length}
            </div>
            <div>
               {questoes[currentQuestion].questionText}
            </div>
          </div>
          <div className='group-respost'>
              {questoes[currentQuestion].answerOptions.map((item, index)=>{
                <button key={index}> {item.answerText} </button>
              })}
          </div>
          </>
        )}
      </div>
  );
}

export default App;
